import React from "react";
import { styled } from "styled-components";

export const Title = styled.div`
  font-size: 18px;
  font-weight: 400;
  line-height: 21px;
  letter-spacing: 0em;
  text-align: center;
  color: #333333;
  width: 70%;
`;

export const SmallerTitle = styled.div`
  font-size: 14px;
  font-weight: 400;
  line-height: 16px;
  letter-spacing: 0em;
  text-align: left;
  color: #333333;
  width: 70%;
`;

export const InsertBtn = styled.button``;

export const TxtBox = styled.input`
  border: 0px;
  font-size: 13px;
  font-weight: 400;
  line-height: 15px;
  letter-spacing: 0em;
  text-align: center;
  width: 70%;
  color: #333333;
  ::placeholder {
    color: #bdbdbd;
  }
`;
