import React, { useState } from "react";
import styled from "styled-components";

import BackIcon from "../Assets/icons/goback.png";
import dummy1 from "../Assets/dumy.png";

import { NavBar, NavBtn, CenterBtn } from "../Components/NavBar";
import BackgroundContainer from "../Components/BackgroundContainer";
import Main from "../Components/Main";
import {
  SmallerTitle,
  TxtBox,
  TextArea,
} from "../Components/postingComponents";
import { TopBar, Title, Back } from "../Components/TopBar";
import {
  ModalBack,
  ModalBox,
  ModalBtn,
  ExitBtn,
} from "../Components/PhotoModal";

export const Home = () => {
  const [modalOpen, setModalOpen] = useState(false);

  return (
    <div>
      <BackgroundContainer>
        <TopBar>
          <NavBtn>
            <img src={BackIcon} alt="뒤로가기" />
          </NavBtn>
          <Title>네컷 등록</Title>
        </TopBar>
        <MiniContainer>
          <img src={dummy1} />
          <SmallerTitle>When</SmallerTitle>
          <TxtBox type="text" placeholder="ex) 2023. 05. 11" />
          <SmallerTitle>Where</SmallerTitle>
          <TxtBox type="text" placeholder="이화여자대학교" />
          <SmallerTitle>Whom</SmallerTitle>
          <SmallerTitle>What</SmallerTitle>
          <TextArea placeholder="내용을 입력해주세요" />
        </MiniContainer>
        <>
          <CenterBtn onClick={() => setModalOpen(true)} />

          {modalOpen && (
            <ModalBack
              className={"modal-container"}
              ref={modalBackground}
              onClick={(e) => {
                if (e.target === modalBackground.current) {
                  setModalOpen(false);
                }
              }}
            >
              <div className={"modal-content"}>
                <p>리액트로 모달 구현하기</p>
                <button
                  className={"modal-close-btn"}
                  onClick={() => setModalOpen(false)}
                >
                  모달 닫기
                </button>
              </div>
            </ModalBack>
          )}
        </>
        <NavBar />
      </BackgroundContainer>
    </div>
  );
};

export default Home;

export const MiniContainer = styled(Main)`
  width: 80%;
  display: flex;
  height: calc(100vh-140px);
  justify-content: center;
  overflow-x: hidden;
  overflow-y: scroll;
  ::-webkit-scrollbar {
    display: none;
  }
  -ms-overflow-style: none;
  padding: 0px;
`;
